       "dua_indopak": "\u0627\u064e\u0644\u0644\u0651\u0670\u0647\u064f\u0645\u0651\u064e \u0627\u064e\u0635\u0652\u0644\u0650\u062d\u0652 \u0644\u0650\u064a\u0652 \u062f\u0650\u064a\u0652\u0646\u0650\u064a\u064e \u0627\u0644\u0651\u064e\u0630\u0650\u064a\u0652 \u0647\u064f\u0648\u064e \u0639\u0650\u0635\u0652\u0645\u064e\u0629\u064f \u0627\u064e\u0645\u0652\u0631\u0650\u064a\u0652\u060c \u0648\u064e\u0627\u064e\u0635\u0652\u0644\u0650\u062d\u0652 \u0644\u0650\u064a\u0652 \u062f\u064f\u0646\u0652\u064a\u064e\u0627\u064a\u064e \u0627\u0644\u0651\u064e\u062a\u0650\u064a\u0652 \u0641\u0650\u064a\u0652\u0647\u064e\u0627 \u0645\u064e\u0639\u064e\u0627\u0634\u0650\u064a\u0652\u060c \u0648\u064e\u0627\u064e\u0635\u0652\u0644\u0650\u062d\u0652 \u0644\u0650\u064a\u0652 \u0627\u0670\u062e\u0650\u0631\u064e\u062a\u0650\u064a \u0627\u0644\u0651\u064e\u062a\u0650\u064a\u0652 \u0641\u0650\u064a\u0652\u0647\u064e\u0627 \u0645\u064e\u0639\u064e\u0627\u062f\u0650\u064a\u0652\u060c \u0648\u064e\u0627\u062c\u0652\u0639\u064e\u0644\u0650 \u0627\u0644\u0652\u062d\u064e\u064a\u064e\u0627\u0629\u064e \u0632\u0650\u064a\u064e\u0627\u062f\u064e\u0629\u064b \u0644\u0650\u0651\u064a\u0652 \u0641\u0650\u064a\u0652 \u0643\u064f\u0644\u0651\u0650 \u062e\u064e\u064a\u0652\u0631\u064d\u060c \u0648\u064e\u0627\u062c\u0652\u0639\u064e\u0644\u0650 \u0627\u0644\u0652\u0645\u064e\u0648\u0652\u062a\u064e \u0631\u064e\u0627\u062d\u064e\u0629\u064b \u0644\u0650\u0651\u064a\u0652 \u0645\u0650\u0646\u0652 \u0643\u064f\u0644\u0651\u0650 \u0634\u064e\u0631\u0651\u064d",
       "transliteration": "\u0986\u09b2\u09cd\u09b2\u09be-\u09b9\u09c1\u09ae\u09cd\u09ae\u09be, \u0986\u09b8\u09cd\u09ac\u09b2\u09bf\u2019\u09b9 \u09b2\u09c0 \u09a6\u09c0\u09a8\u09bf\u09af\u09bc\u09be\u09b2\u09cd\u09b2\u09be\u09af\u09c0 \u09b9\u09c1\u0993\u09df\u09be '\u0987\u09b8\u09cd\u09ac\u09cd\u200c\u09ae\u09be\u09a4\u09c1 \u0986\u09ae\u09b0\u09c0\u0964 \u0993\u09af\u09bc\u09be \u0986\u09b8\u09cd\u09ac\u09b2\u09bf\u2019\u09b9 \u09b2\u09c0 \u09a6\u09c1\u09a8\u09cd\u200c\u0987\u09af\u09bc\u09be \u0987\u09df\u09be\u09b2\u09cd\u09b2\u09be\u09a4\u09c0 \u09ab\u09c0\u09b9\u09be \u09ae\u09be\u2019\u0986-\u09b6\u09c0\u0964 \u0993\u09af\u09bc\u09be \u0986\u09b8\u09cd\u09ac\u09b2\u09bf\u2019\u09b9 \u09b2\u09c0 \u0986\u0996\u09bf\u09b0\u09be\u09a4\u09bf\u2018\u09b2\u200c \u09b2\u09be\u09a4\u09c0 \u09ab\u09bf\u09b9\u09be \u09ae\u09be\u0986\u2018\u09a6\u09bf \u0993\u09df\u09be\u099c-\u2018\u0986\u09b2\u09bf\u09b2 '\u09b9\u09be\u09df\u09be\u09a4\u09be \u099d\u09bf\u09df\u09be\u2018\u09a6\u09be\u09a4\u09be\u09a8 \u09b2\u09c0 \u09ab\u09bf \u0995\u09c1\u09b2\u09cd\u09b2\u09bf \u0996\u09be\u0987\u09b0\u09bf\u09a8 \u0993\u09df\u09be\u099c-\u2018\u0986\u09b2\u09bf\u09b2 \u09ae\u09be\u0993\u09a4\u09be \u09b0\u2019\u09b9\u09be\u09a4\u09be\u09a8 \u09b2\u09c0 \u09ae\u09bf\u09a8 \u0995\u09c1\u09b2\u09cd\u09b2\u09bf \u09b6\u09be\u09b0\u09cd\u200c",
